pragma solidity >=0.5.12 <0.7.0;
pragma experimental ABIEncoderV2;

import "./CenterControl.sol";
import "./ErrorModule.sol";
import "./InfoDB.sol";
import "./SafeMath.sol";
import "./ResourceDB.sol";

contract AuthorModule is ErrorModule {
    using SafeMath for uint256;

    CenterControl public Center_;
    InfoDB public Info_;

    event LogNewResourceResID(bytes16 resDb_);
    event LogMaiveChange(bool success);

    /**
     * @dev 
     * @param _center address : 
     * @param _info   address : 
     */
    constructor(address _center, address _info) public {
        Center_ = CenterControl(_center);
        Info_ = InfoDB(_info);
    }

    /**
     * @dev 
     * @dev 
     * @param _ipfs      string  : UDFS Hash
     * @param _thubanail :
     * @param _cover :
     * @param _title :
     */
    function publish(
        string memory _ipfs,
        string[] memory _thubanail,
        string memory _cover,
        string memory _title
    ) public {
        // if (_hashFilter(_ipfs) == false) {
        //     emit LogError(RScorr.InvalidUdfs);
        //     return false;
        // }

        // 
        //return Center_.createClaim(_udfs, msg.sender, _price, _type, true);\]
        bytes16  resId = Center_.createResource(
                _ipfs,
                msg.sender,
                _thubanail,
                _cover,
                _title
            );
        emit LogNewResourceResID(resId);
    }

    /**
     * @dev 
     * @param _sId  bytes16 : 
     * @param _ipfs  string  : udfs
     * @param _thubanail :
     * @param _cover :
     * @param _title :
     * @return          bool    :  true
     */
    function updateResource(
        bytes16 _sId,
        string memory _ipfs,
        string[] memory _thubanail,
        string memory _cover,
        string memory _title
    ) public returns (bool) {
        return
            Center_.updateResource(
                _sId,
                msg.sender,
                _ipfs,
                _thubanail,
                _cover,
                _title
            );
    }

    /**
     * @dev 
     * @param _resId bytes16 : 
     */
    function changeResourceWaive(bytes16 _resId,bool waive) public {
        bool success = Center_.updateResourceWaive(_resId, msg.sender, waive);
        emit LogMaiveChange(success);
    } //TODO : How?

    /////////////////////////
    /// View
    /////////////////////////

    /**
     * @dev 
     * @return bytes16[] :claimId
     */
    function myResource() public view returns (bytes16[] memory) {
        return resourceByAddress(msg.sender);
    }

    /**
     * @dev 
     * @return  bytes16[] :claimId
     */
    function resourceByAddress(address _author)
        public
        view
        returns (bytes16[] memory)
    {
        return Info_.getResourceByAuthor(_author);
    }

    /**
     * @dev 
     * @return address[] :
     */
    function consumerByClaim(bytes16 _resId)
        public
        view
        returns (address[] memory)
    {
        return Info_.getConsumerByResource(_resId);
    }

    /**
     * @dev 
     * @param _resID   bytes16 : id
     */
    function findResourceInfo(bytes16 _resID)
        public
        view
        returns (ResourceDB.Resource memory
            // address author,
            // string memory ipfs,
            // uint256 initDate,
            // bool waive,
            // uint256 gratuityNum,
            // string memory _cover,
            // string memory _title,
            //  string[] memory _thubanail
        )
    {
        return Center_.getResourceDetails(_resID);
    }

    /**
     * @dev 
     * @param _orderId bytes32  : ID
     */
    function findOrderInfo(bytes32 _orderId)
        public
        view
        returns (
            uint256 time,
            uint256 value,
            address author,
            address payer,
            bytes16 resId
        )
    {
        return Center_.getOrderDetails(_orderId);
    }

    ///////////////////////
    /// Internal Function
    //////////////////////

    // /**
    //  * @dev HASH
    //  * @param _input string  : 
    //  * @return bool          : true
    //  */
    // function _hashFilter(string memory _input) internal pure returns (bool) {
    //     bytes memory _temp = bytes(_input);

    //     // length == 46 , Qm~
    //     if (_temp.length == 46 && _temp[0] == 0x51 && _temp[1] == 0x6D) {
    //         return true;
    //     }
    //     return false;
    // }

}
