pragma solidity >=0.5.12 <0.7.0;
pragma experimental ABIEncoderV2;

import "./CenterControl.sol";
import "./ErrorModule.sol";
import "./InfoDB.sol";
import "./SafeMath.sol";
import "./ResourceDB.sol";

contract AuthorModule is ErrorModule {
    using SafeMath for uint256;

    CenterControl public Center_;
    InfoDB public Info_;

    /**
     * @dev 构造函数
     * @param _center address : 中心合约地址
     * @param _info   address : 用户信息数据合约地址
     */
    constructor(address _center, address _info) public {
        Center_ = CenterControl(_center);
        Info_ = InfoDB(_info);
    }

    /**
     * @dev 声明一个新的资源
     * @dev 扣取押金到资源池，调用中心出版社接口，发布资源。
     * @param _ipfs      string  : 资源的UDFS Hash值
     * @param _thubanail :
     * @param _cover :
     * @param _title :
     * @return           bool    : 操作成功返回 true
     */
    function publish(
        string memory _ipfs,
        string[] memory _thubanail,
        string memory _cover,
        string memory _title
    ) public payable returns (bool) {
        // if (_hashFilter(_ipfs) == false) {
        //     emit LogError(RScorr.InvalidUdfs);
        //     return false;
        // }

        //向中心发布一个资源， 这种方式是由平台层发布资源。
        //return Center_.createClaim(_udfs, msg.sender, _price, _type, true);
        if (
            Center_.createResource(
                _ipfs,
                msg.sender,
                _thubanail,
                _cover,
                _title
            )
        ) {
            return true;
        } else {
            return false;
        }
    }

    /**
     * @dev 更新一个属于自己的资源的内容
     * @param _sId  bytes16 : 资源的索引
     * @param _ipfs  string  : 新的udfs
     * @param _thubanail :
     * @param _cover :
     * @param _title :
     * @return          bool    : 操作成功返回 true
     */
    function updateResource(
        bytes16 _sId,
        string memory _ipfs,
        string[] memory _thubanail,
        string memory _cover,
        string memory _title
    ) public returns (bool) {
        return
            Center_.updateResource(
                _sId,
                msg.sender,
                _ipfs,
                _thubanail,
                _cover,
                _title
            );
    }

    /**
     * @dev 放弃一个属于自己的资源
     * @param _resId bytes16 : 资源的索引
     * @return         bool    : 操作成功返回 true
     */
    function abandonResource(bytes16 _resId) public returns (bool) {
        return Center_.updateResourceWaive(_resId, msg.sender, true);
    } //TODO : 放弃的资源，处理问题，如果作者想要重新发布。。会提示资源存在？How?

    /////////////////////////
    /// View
    /////////////////////////

    /**
     * @dev 查询调用者发布过的所有资源
     * @return bytes16[] :由claimId组成的列表
     */
    function myResource() public view returns (bytes16[] memory) {
        return resourceByAddress(msg.sender);
    }

    /**
     * @dev 查询指定地址发布过的所有资源
     * @return  bytes16[] :由claimId组成的列表
     */
    function resourceByAddress(address _author)
        public
        view
        returns (bytes16[] memory)
    {
        return Info_.getResourceByAuthor(_author);
    }

    /**
     * @dev 查询资源的所有购买者地址
     * @return address[] :由购买者地址组成的列表
     */
    function consumerByClaim(bytes16 _resId)
        public
        view
        returns (address[] memory)
    {
        return Info_.getConsumerByResource(_resId);
    }

    /**
     * @dev 查询一个资源的详细信息
     * @param _resID   bytes16 : 资源id
     */
    function findResourceInfo(bytes16 _resID)
        public
        view
        returns (ResourceDB.Resource memory
            // address author,
            // string memory ipfs,
            // uint256 initDate,
            // bool waive,
            // uint256 gratuityNum,
            // string memory _cover,
            // string memory _title,
            //  string[] memory _thubanail
        )
    {
        return Center_.getResourceDetails(_resID);
    }

    /**
     * @dev 获取订单的详细信息
     * @param _orderId bytes32  : 订单ID
     */
    function findOrderInfo(bytes32 _orderId)
        public
        view
        returns (
            uint256 time,
            uint256 value,
            address author,
            address payer,
            bytes16 resId
        )
    {
        return Center_.getOrderDetails(_orderId);
    }

    ///////////////////////
    /// Internal Function
    //////////////////////

    // /**
    //  * @dev 简单的检查一下HASH串的有效性
    //  * @param _input string  : 待检测的字符串
    //  * @return bool          : 检测通过返回true
    //  */
    // function _hashFilter(string memory _input) internal pure returns (bool) {
    //     bytes memory _temp = bytes(_input);

    //     // length == 46 , Qm~
    //     if (_temp.length == 46 && _temp[0] == 0x51 && _temp[1] == 0x6D) {
    //         return true;
    //     }
    //     return false;
    // }

}

