pragma solidity >=0.5.12 <0.7.0;
pragma experimental ABIEncoderV2;

import "./WhiteMange.sol";
import "./SafeMath.sol";

import "./ResourceDB.sol";
import "./OrderDB.sol";
import "./InfoDB.sol";

//TODO: TO modify ERC20 to GAS;

/*
    支付验证：
    1. 保证资源存在，避免对无效的资源付费
    2. 检查定价不为0，定价为0的资源，不需要付费
    3. 检查资源是否已经放弃，放弃的资源不能购买，资源下架，保证已购买的用户能继续查看
    4. 检查订单是否已存在，避免反复购买
    5. 检查支付地址的购买能力
*/

/**
 * @title 中心合约
 * @dev 本合约起一个纽带作用
 * @notice 合约中
 */
contract CenterControl is WhiteMange {
    using SafeMath for uint256;
    //Payment public pay_;

    ResourceDB public resDb_;
    OrderDB public orderDb_;
    InfoDB public infoDb_;

    /**
     * @dev constructo
     */
    constructor() public {
       owner = msg.sender;
       admin = msg.sender;
        emit  LogAddress("construcor",admin, owner,msg.sender);
    }

    /**
     * @dev Initialization function,related contract of the project
     * @notice Can only be called once by the owner.
     * @param _resource address : ClaimDB's contract address
     * @param _order address : OrderDB's contract address
     * @param _info  address : InfoDB's contract address
     * @return       bool    : The successful call returns true.
     */
    function initialize(address _resource, address _order, address _info)
        public
        onlyAdmin
        returns (bool)
    {
        require(address(resDb_) == address(0), "You can only call it once");
        require(
            _resource != address(0) &&
                _order != address(0) &&
                _info != address(0),
            "contract address cannot be 0."
        );

        resDb_ = ResourceDB(_resource);
        orderDb_ = OrderDB(_order);
        infoDb_ = InfoDB(_info);

        return true;
    }

    // TODO：收的押金的数量，把钱转移到一个更安全的地方。
    // 岂不是可以随便拿走这笔钱么
    //function  () public payable  {
    // 只允许迁移的时候，管理才能动这笔钱。
    //}

    /**
     * @dev  申明一个新资源
     * @param _ipfs    string  : 资源的IPFS Hash值
     * @param _author  address : 发布者的地址
     * @param _thubanail :
     * @param _cover :
     * @param _title :
     * @return         bool    : 操作成功返回true
     */
    function createResource(
        string memory _ipfs,
        address _author,
        string[] memory _thubanail,
        string memory _cover,
        string memory _title
    ) public returns (bool) {
        if (whitelist_[msg.sender] != true) {
            emit LogError(RScorr.Insufficient);
            return false;
        }

        // if (_storage == false) {
        //     emit LogSimpleClaim(_author, _fs);
        //     return false;
        // }

        bytes16 _resId = bytes16(keccak256(abi.encodePacked(_ipfs, _author)));
        if (resDb_.isExist(_resId)) {
            emit LogError(RScorr.ObjExist);
            return false;
        } /* Check if the resource exists */

        require(
            resDb_.insertResource(
                _resId,
                _ipfs,
                _author,
                _thubanail,
                _cover,
                _title
            ),
            "resource create failed"
        );

        require(infoDb_.insertResource(_resId, _author), "");

        return true;

    }

    /**
     * @dev  创建一个新订单
     * @param _author address : 购买者地址
     * @param _resId  bytes16 : 商品ID
     * @param _value  uint256  : 支付者地址
     * @param _payer  address : 定价
     */
    function createOrder(
        address _author,
        bytes16 _resId,
        uint256 _value,
        address _payer
    ) public returns (bool succ, string memory info) {
        if (whitelist_[msg.sender] != true) {
            emit LogError(RScorr.Insufficient);
            return (false, "not in whitelist");
        } // Check the caller's whitelist permission.

        if (!resDb_.getResourceWaive(_resId)) {
            emit LogError(RScorr.InvalidObj);
            return (false, "resource have  shelves");
        } //

        // 生成订单id
        bytes32 _orderId = bytes32(keccak256(abi.encodePacked(_payer, _resId)));
        resDb_.addResourceGratuityNum(_resId, _value);

        // 生成订单
        if (orderDb_.insert(_orderId, _author, _resId, _payer, _value)) {
            require(
                infoDb_.insertOrder(_orderId, _resId, _payer),
                "order insert into info  error"
            );
            return (true, "");
        } else {
            return (false, "FAILE");
        }
    }

    // /*
    //  * @dev  判断是否已经打赏
    //  * @param _author address : 购买者地址
    //  * @param _claimId  bytes16 : 商品ID
    //  * @param _payer    address : 支付者地址
    //  * @return          bool    : 操作成功返回true
    // */
    // function isPayed(address _author, bytes16 _resId, address _payer)
    //     public
    //     view
    //     returns (bool)
    // {
    //     bytes32 _orderId = bytes32(keccak256(abi.encodePacked(_payer, _resId)));
    //     return orderDb_.isExist(_orderId);
    // }

    ///@dev 更新资源内容
    function updateResource(
        bytes16 _sid,
        address _author,
        string memory _newipfs,
        string[] memory _thubanail,
        string memory _cover,
        string memory _title
    ) public returns (bool) {
        if (whitelist_[msg.sender] != true) {
            emit LogError(RScorr.Insufficient);
            return false;
        } // 检查调用者白名单权限

        return
            resDb_.updateResource(
                _sid,
                _newipfs,
                _author,
                _thubanail,
                _cover,
                _title
            );
    }

    ///@dev 放弃资源，作者模块只能放弃，管理员能取消资源放弃。
    function updateResourceWaive(bytes16 _cid, address _author, bool _waive)
        public
        returns (bool)
    {
        if (whitelist_[msg.sender] != true) {
            emit LogError(RScorr.Insufficient);
            return false;
        } // 检查调用者白名单权限

        if (resDb_.updateResourceWaive(_cid, _author, _waive)) {
            return true;
        }
        return false;
    }

    /////////////////////////
    /// Internal
    /////////////////////////

    ///@dev 根据ResId查找资源的详细信息
    function getResourceDetails(bytes16 _resid)
        public
        view
        returns (ResourceDB.Resource memory)
    // address author,
    // string memory ipfs,
    // uint256 initDate,
    // bool waive,
    // uint256 gratuityNum,
    // string memory _cover,
    // string memory _title,
    // string[] memory _thubanail
    {
        return resDb_.getResourceInfoByID(_resid);
    }

    ///@dev 根据OrderId查找订单的详细信息
    function getOrderDetails(bytes32 _oid)
        public
        view
        returns (
            uint256 time,
            uint256 value,
            address author,
            address payer,
            bytes16 resId
        )
    {
        return orderDb_.getOrderInfoByID(_oid);
    }

    function getResourceAutherInfo(bytes16 _sid) public view returns (address) {
        return resDb_.getAuthor(_sid);
    }

}

