pragma solidity >=0.5.12 <0.7.0;

import "./WhiteMange.sol";

/**
 * @title InfoDB
 * @dev Used to query.
 * @notice Whitelist permission is required for data writing
 */
contract InfoDB is WhiteMange{

    // list, uint8=256
    // list
    // list
    // TODO:>10000

    struct stUserInfo{
        //uint256 orderCnt;
        bytes32[] orderList;   // All orders from user.
    }

    struct stAuthorInfo{
        bytes16[] resource;       // All resources published by the author
    }

    struct stResouceInfo{
        mapping(address => bytes32) bought; // Query the order by a user on this resource.
        address[] buyers;                   // All users from resources.
        bytes32[] orderList;                // All orders for this resource
    }

    mapping(address => stUserInfo) user_;
    mapping(bytes16 => stResouceInfo) res_;
    mapping(address => stAuthorInfo) author_;

    constructor () public {
    //    owner = msg.sender;
    //    admin = msg.sender;
        emit  LogAddress("construcor",admin, owner,msg.sender);
    }

    /**
     * @dev Record an order.
     * @param _oId   bytes32  : Orders index(OrderID).
     * @param _sid   bytes16  : Resource index(ClaimID).
     * @param _buyer address  : Buyer's address
     * @return       bool     : The successful call returns true.
     */
    function insertOrder(bytes32 _oId, bytes16 _sid, address _buyer)
        public
        returns(bool)
    {
        if (whitelist_[msg.sender] != true){
            emit LogError(RScorr.Insufficient);
            return false;
        } // Check the caller's whitelist permission.

        user_[_buyer].orderList.push(_oId);
        // 
        res_[_sid].buyers.push(_buyer);
        // 
        res_[_sid].bought[_buyer] = _oId;

        return true;
    }

    /**
     * @dev Record a resource generation.
     * @param _sid    bytes16  : Resource index(ClaimID).
     * @param _author address  : Authors' address
     * @return        bool     : The successful call returns true.
     */
    function insertResource(bytes16 _sid, address _author)
        public
        returns(bool)
    {
        if (whitelist_[msg.sender] != true){
            emit LogError(RScorr.Insufficient);
            return false;
        } // Check the caller's whitelist permission.

        author_[_author].resource.push(_sid);
        return true;
    }

    /////////////////////////
    /// View
    /////////////////////////

    /**
     * @dev Query all resources the author has published.
     * @param _author  address   : Author's address
     * @return         bytes16[] : claimIDbytes16
     */
    function getResourceByAuthor(address _author) public view  returns(bytes16[] memory){
        return author_[_author].resource;
    }

    /**
     * @dev 
     * @param _buyer   address   : User's address
     * @return         bytes32[] : orderIDbytes32
     */
    function getOrdersByUser(address _buyer) public view  returns(bytes32[] memory){
        return user_[_buyer].orderList;
    }

    /**
     * @dev 
     * @param _sid     bytes16   :  claimID
     * @return         address[] : 
     */
    function getConsumerByResource(bytes16 _sid)  public view  returns(address[] memory){
        return res_[_sid].buyers;
    }


    //TODO:
    /// 
//    function getOrdersByClaim(bytes16 _cid) view public returns(){
//        return claim_[_cid][orderList];
//    }


    /**
     * @dev Query whether a particular user has purchased a resource
     * @param _buyer address : User's address
     * @param _sid bytes16   : id
     * @return bool          : truefalse;
     */
    function isBought(address _buyer, bytes16 _sid)  public view  returns(bool){
        // 
        if (res_[_sid].bought[_buyer] == 0 ){
            return false;
        }
        return true;
    }
}

