pragma solidity >=0.5.12 <0.7.0;

import "./SafeMath.sol";
import "./CenterControl.sol";
import "./InfoDB.sol";

contract UserModule {
    using SafeMath for uint256;

    CenterControl public Center_;
    InfoDB public Info_;

    /**
     * @dev 构造函数
     * @param _center address : 中心合约地址
     * @param _info   address : 信息数据合约地址
     */
    constructor(address _center, address _info) public {
        Center_ = CenterControl(_center);
        Info_ = InfoDB(_info);
    }

    /**
     * @dev 打赏视频
     * @param _resId bytes16 : 资源id
     * @return         bool    : 操作成功返回true
     */
    function buy(bytes16 _resId,uint256 value) public payable returns (bool) {
        address  _author = Center_.getResourceAutherInfo(_resId);
        payable(_author).transfer(value);
        bool succ = false;
        (succ,) = Center_.createOrder(_author,_resId,value,msg.sender);
        if (succ == true) {
            return true;
        } else {
            return false;
        }
    }

    /////////////////////////
    /// View
    /////////////////////////

    /**
     * @dev 查询用户自己资源
     * @return claims bytes16[] : 由claimID组成的列表
     */
    function myGoods() public view returns (bytes16[] memory) {
        return Info_.getResourceByAuthor(msg.sender);
    }

    /**
     * @dev 查询用户自己所有的订单
     * @return claims bytes32[] : 由orderID组成的列表
     */
    function myOrders() public view returns (bytes32[] memory) {
        return Info_.getOrdersByUser(msg.sender);
    }

    /**
     * @dev 获取订单的详细信息
     * @param _orderId bytes32  : 订单ID
     */
    function findOrderInfo(bytes32 _orderId)
        public
        view
        returns (
            uint256 time,
            uint256 value,
            address author,
            address payer,
            bytes16 resId
        )
    {
        return Center_.getOrderDetails(_orderId);
    }

}

